import React, { useState } from "react";
import { FirebaseContext } from "./context/FirebaseContext";



export default function SignUpForm() {

    const {firebase} = useContext(FirebaseContext);

    const [email, setEmail] = useState("");   
    const [pwd, setPwd] = useState(""); 
    return(
        <div className="signupbar">
            <form onSubmit = {(event) => { 
                event.preventDefault;
                firebase.auth().createUserWithEmailAndPassword(email, pwd).then(() => alert("signed up")).catch(() => alert(error.message));
                setEmail("");
                setPwd("");
                }}
            >
                <label>Email</label>
                <input type="email" value={email} onChange={(event) => setEmail(event.target.value)}></input>
                <label>Password</label>
                <input type="password" value={pwd} onChange={(event) => setPwd(event.target.value)}></input>
                <button type="submit">Sign Up</button>
            </form>
        </div>
    );
}
