import React from 'react';
import { initializeApp } from 'firebase/app';
import { getFirestore } from 'firebase/firestore';
import './App.css';

const firebaseConfig = {
  apiKey: "AIzaSyDX1CXAtkqj9Tg1HVYwlcocpkMxB-Y4NTw",
  authDomain: "spotifybuds-e7e29.firebaseapp.com",
  databaseURL: "https://spotifybuds-e7e29-default-rtdb.firebaseio.com",
  projectId: "spotifybuds-e7e29",
  storageBucket: "spotifybuds-e7e29.appspot.com",
  messagingSenderId: "566960468786",
  appId: "1:566960468786:web:cb73617f63fc5f79e45d2b",
  measurementId: "G-N3X3577Z70"
};

const app = initializeApp(firebaseConfig);
const db = getFirestore(app);

class App extends React.Component {

 constructor(props) {
   super(props);
   this.state = {
     latitude: null,
     longitude: null,
     userAddress: null
   };
   this.getLocation = this.getLocation.bind(this);
   this.getCoordinates = this.getCoordinates.bind(this); // Fix the binding here
   this.getUserAddress = this.getUserAddress.bind(this);
   this.getAllInputs = this.getAllInputs.bind(this);
   this.handleInsertData = this.handleInsertData.bind(this);
 }

 componentDidMount() {
   this.getLocation();
   this.interval = setInterval(this.getLocation, 1000); // Update every 1 seconds
 }

 componentWillUnmount() {
   clearInterval(this.interval); // Clear the interval when the component is unmounted
 }


 getLocation() {
   if (navigator.geolocation) {
     navigator.geolocation.getCurrentPosition(this.getCoordinates, this.handleLocationError);
   } else {
     alert("Geolocation is not supported by this browser.");
   }
 }


 getCoordinates(position) {
 this.setState(
   {
     latitude: position.coords.latitude,
     longitude: position.coords.longitude
   },
   this.getUserAddress
 );
}


getUserAddress() {
 const { latitude, longitude } = this.state;
 fetch(`https://maps.googleapis.com/maps/api/geocode/json?latlng=${latitude},${longitude}&key=AIzaSyBxDQfDy1nwLkmwaJUfQtNNqXla3kZr-ug`)
   .then(response => response.json())
   .then(data => {
     const address = data.results[0].formatted_address;
     this.setState({ userAddress: address });
   })
   .catch(error => alert(error));
}


 handleLocationError(error){
   switch(error.code) {
     case error.PERMISSION_DENIED:
       alert("User denied the request for Geolocation.")
       break;
     case error.POSITION_UNAVAILABLE:
       alert("Location information is unavailable.")
       break;
     case error.TIMEOUT:
       alert("The request to get user location timed out.")
       break;
     case error.UNKNOWN_ERROR:
       alert("An unknown error occurred.")
       break;
     default:
       alert("An unknown error occurred.")
   }
 }

 getAllInputs(){
    return{
      latitude: this.state.latitude,
      longitude: this.state.longitude,
      userAddress: this.state.userAddress
    }
 }

 handleInsertData() {
  const db = this.state.db;
  const data = this.getAllInputs();

  this.setState(ref(db, 'User'),
  {
    Latitude: data.latitude,
    Longitude: data.longitude,
    Address: data.address
  });
 }
//  handleInsertData() {
//   const { latitude, longitude, userAddress } = this.state;

//   if (!latitude || !longitude || !userAddress) {
//     alert('Missing location data');
//     return;
//   }

//   const data = {
//     latitude: Number(latitude),
//     longitude: Number(longitude),
//     address: userAddress,
//   };

//   db.collection('locations')
//     .add(data)
//     .then(() => {
//       alert('Data was added successfully');
//     })
//     .catch((error) => {
//       alert('There was an error, details: ' + error);
//     });
// }

 render() {
   return (
     <div className="App">
       <h2>
         Geolocation Finder for SpotifyBuds
       </h2>
       <button onClick={this.getLocation}>Get coordinates</button>
       <h4>Your Coordinates</h4>
       <p>Latitude: {this.state.latitude}</p>
       <p>Longitude: {this.state.longitude}</p>
       <h4>Finding people around your area</h4>
       <p>Address: {this.state.userAddress}</p>
       {
         this.state.latitude && this.state.longitude &&
         <img src={`https://maps.googleapis.com/maps/api/staticmap?center=${this.state.latitude},${this.state.longitude}&zoom=13&size=600x300&maptype=roadmap&markers=color:blue%7Clabel:S%7C40.702147,-74.015794&markers=color:green%7Clabel:G%7C40.711614,-74.012318&markers=color:red%7C${this.state.latitude},${this.state.longitude}&key=AIzaSyBxDQfDy1nwLkmwaJUfQtNNqXla3kZr-ug`} alt=''/>
       }
     </div>
   )
 }
}
export default App;
